export async function middleware(request: NextRequest) {
  const { pathname } = request.nextUrl;

  // Skip middleware for static assets and API routes
  if (
    pathname.startsWith('/_next') ||
    pathname.startsWith('/api') ||
    pathname.startsWith('/static') ||
    pathname.match(/\.(ico|png|jpg|jpeg|gif|svg|webp|css|js|json|xml|txt)$/)
  ) {
    return NextResponse.next();
  }

  // Load URL mappings
  const mappings = await loadUrlMappings();

  // Check for exact match in mappings
  if (mappings.has(pathname)) {
    const redirectTo = mappings.get(pathname)!;
    console.log(`[REDIRECT] ${pathname} → ${redirectTo}`);

    // Track redirect (async, don't wait)
    trackRedirect(pathname, redirectTo).catch(console.error);

    // Return 301 redirect
    const url = request.nextUrl.clone();
    url.pathname = redirectTo;
    return NextResponse.redirect(url, { status: 301 });
  }

  // Check for pattern-based redirects
  const patternRedirect = getRedirectUrl(pathname);
  if (patternRedirect) {
    console.log(`[REDIRECT] ${pathname} → ${patternRedirect} (pattern)`);

    // Track redirect (async, don't wait)
    trackRedirect(pathname, patternRedirect).catch(console.error);

    // Return 301 redirect
    const url = request.nextUrl.clone();
    url.pathname = patternRedirect;
    return NextResponse.redirect(url, { status: 301 });
  }

  // No redirect needed
  return NextResponse.next();
}

// Configure which routes should run the middleware
export const config = {
  matcher: [
    /*
     * Match all request paths except:
     * - _next/static (static files)
     * - _next/image (image optimization files)
     * - favicon.ico (favicon file)
     * - public files (public directory)
     */
    '/((?!_next/static|_next/image|favicon.ico|.*\\..*|public).*)',
  ],
};
